const{chromium}=require("playwright"),fs=require("fs"),path=require("path"),main=fs.readFileSync(path.join(__dirname,"webScript.js"),"utf8"),filePath=path.resolve(process.cwd(),process.argv[2]),destDir=process.argv[3];console.log("Running Playwright..."),(async()=>{const e=await chromium.launch(),a=await e.newPage();await a.goto(`file:///${filePath.replace(/\\/g,"/")}`),await a.evaluate(`${main}`);const t=`<html>${await a.innerHTML("html")}</html>`;console.log("HTML Length: "+t.length),await e.close();const i=path.join(destDir,path.basename(filePath));console.log(i),fs.writeFile(i,t,(e=>{if(e)throw e;console.log("Done")}))})();